import React from 'react';
import ListItem from './ListItem';
import './Note.css'
class Note extends React.Component {
    state={
        items:[],
        currentItem:{
            text:'',
            key:''
        }
    }

    handleInput =(e) => {
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        })
    }



    render(){
        return(
            <div className="form">
                <header>
                    <form id="to-do-form">
                        <input 
                            onSubmit={()=> this.props.addItem(this.currentItem.text)}
                            type="text" 
                            placeholder="Enter Text" 
                            value={this.state.currentItem.text}
                            onChange={this.handleInput}
                        />
                        <button type="submit">Add</button>
                    </form>
                </header>
                <ListItem />
            </div>

        )
    }
}

export default Note
