import React from 'react';
import ListItem from './ListItem';
import './Note.css'
class Note extends React.Component {
    state={
        currentItem:{
            text:'',
            key:''
        }
    }

    onNoteChange = (event) =>{

        this.setState({
            currentItem:{
                text:event.target.value
            }
        })
        console.log(this.state.currentItem.text)
      }

    submitForm = (e) => {
        e.preventDefault()
        this.props.addItem(this.state.currentItem) 
    }

    render(){
        return(
            <div className="form">
                <header>
                    <form id="to-do-form" onSubmit={this.submitForm}>
                        <input 
                            type="text" 
                            placeholder="Enter Text" 
                            value={this.state.currentItem.text}
                            onChange={this.onNoteChange}
                        />
                        <button type="submit">Add</button>
                    </form>
                </header>
                <ListItem />
            </div>

        )
    }
}

export default Note
