import React from 'react'
import Particles from 'react-particles-js'
import './ListItem.css'
import Note from './Note'
import {Button, ButtonToolbar} from 'react-bootstrap'
import {EditModal} from './EditModal'


class ListItem extends React.Component{

    state = {
        editModalShow: false

    }


    whenClickEdit = ()=> {
        this.setState({editModalShow:true})
    }    

    render(){
        let editModalClose  = () => this.setState({editModalShow:false})
        return <div>
            {this.props.items.map(item => {
                return(
                    <div>
                        <div className="list" key={item.noteid} onDoubleClick={this.changeEditMode} >
                            <p key={item.noteid}> {item.todo} {item.noteid} </p>
                            <div className="icons">
                                <ButtonToolbar>
                                    <Button
                                        variant ='primary'
                                        onClick ={this.whenClickEdit}
                                    >
                                        Edit
                                    </Button>
                                    <EditModal items={this.props.items.noteid} show={this.state.editModalShow} onHide={editModalClose} />
                                </ButtonToolbar>
                                <img onClick={()=> this.props.deleteItem(item.noteid)} src="https://img.icons8.com/wired/64/000000/empty-trash.png" height="20" />
                            </div>
                        </div>
                    </div>

                )
            })}
        </div>

    }
}


