import React, { Component } from 'react';
import Particles from 'react-particles-js';
import './App.css';
import Signin from './components/Signin/Signin';
import Navigation from './components/Navigation/Navigation'
import Logo from './components/Logo/Logo'
import Register from './components/Register/Register'
import Note from './components/Notes/Note'
import ListItem from './components/Notes/ListItem'
import axios from 'axios'

const particlesOption={
  particles:{
    polygon: {
      enable: true,
      type: 'inside',
      move: {
          radius: 10
      }
    }
  }
}




class App extends Component {

  state = {
    route:'signin',
    isSignedIn: false,
    user:{
      id:'',
      name:'',
      email:''
    }, 
    currentItem:{
      text:'',
      key:''
    },
    items:[]

  }




  loadUser = (data) =>{

    var obj
    fetch(' http://localhost:8000/todo/' + data.id)
      .then(res => res.json())
      .then(data => obj = data)
      .then(() => this.setState({items:obj}))
    this.setState({
      user:{
        id:data.id,
        name:data.name,
        email:data.email
      },
    })


  }

  onRouteChange = (route) =>{
    if (route === 'signout'){
      this.setState({isSignedIn:false})
    } else if (route ==='home'){
      this.setState({isSignedIn:true})
    }
    this.setState({route : route})
  }

  addItem = (item, id) =>  {
    console.log(item)
    if (item.todo !== ""){
      fetch('http://localhost:8000/add/' + id, {
        method:'post',
        headers:{'Content-Type' : 'application/json'},
        body:JSON.stringify({
          todo:item.todo,
          id:id
        })
      })



      const newItems = [...this.state.items, item]
      this.setState({
        items:newItems
      },() => console.log(this.state.items)) 
    }



  }

  deleteItem = (key) =>{
    console.log(key)
    fetch('http://localhost:8000/todo/' + key, {
      method:'delete',
      headers:{'Content-Type' : 'application/json'},
      body:JSON.stringify({ 
        noteid:key
      })
    })



    console.log(key)

  }

  render(){




    return(
      <div className="App">
        <Navigation isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange}/>
        {this.state.route==='home'
          ? 
            <div>
              <Logo />
              <Particles params={particlesOption} className="particles" />
              <Note deleteItem={this.deleteItem} addItem={this.addItem} items={this.state.items} info={this.state.user} />
            </div>
          : (
            this.state.route === 'signin'
            ? 
              <div>
                <Particles params={particlesOption} className="particles" />
                <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
                </div>
            :
              <div>
                <Particles params={particlesOption} className="particles" />
                <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
              </div>
          )

        }
      </div>
    )
  }
}

export default App;
