import React from 'react';
import ListItem from './ListItem';
import './Note.css'
class Note extends React.Component {
    state={
        items:[],
        currentItem:{
            text:'',
            key:''
        }
    }

    handleInput = (e) => {
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        }, () =>console.log(this.state.currentItem) )

    }

    submitForm = (e) => {
        e.preventDefault()
        this.props.addItem(this.state.currentItem) 
        this.setState({
            currentItem:{
                text:"",
                key:''
            }
        })
    }

    render(){
        return(
            <div className="form">
                <header>
                    <form id="to-do-form" onSubmit={this.submitForm}>
                        <input 
                            type="text" 
                            placeholder="Enter Text" 
                            value={this.state.currentItem.text}
                            onChange={this.handleInput}
                        />
                        <button type="submit">Add</button>
                    </form>
                </header>
                <ListItem items={this.props.items} deleteItem={this.props.deleteItem}/>
            </div>

        )
    }
}

export default Note
