import React from 'react'
import Particles from 'react-particles-js'
import './ListItem.css'
import Note from './Note'
import {Modal, Button, Row, Col, Form } from 'react-bootstrap'


class ListItem extends React.Component{

    state = {
        show: false,
        currentNoteID: ""
    }

    handleModal =() =>{
        this.setState({show:!this.state.show, currentNoteID:this.props.currentItem.key})
        console.log(this.props)
        console.log(this.state)
    }

    handleSubmit = (e) => {
        let noteId = ""
        noteId = document.getElementById("currentNoteId").innerHTML
        const todo = e.target.TodoName.value
        e.preventDefault()
        this.props.editItem(todo, noteId)
    }

    // handleSubmit = (e) =>{
    //     e.preventDefault()
    //     const noteId = document.getElementById("currentNoteId").innerHTML
    //     const todo = e.target.TodoName.value
    //     console.log(todo)
    //     fetch('http://localhost:8000/todo/' + noteId, {
    //     method:'PUT',
    //     headers:{'Content-Type' : 'application/json', 'Authorization' : 'sdfsfsfwiiowuerewrwrewrww'},
    //     body:JSON.stringify({
    //       todo:todo
    //     })
    //   }).then((result)=>console.log(result))
    // }


    render(){

        return <div>
            {this.props.items.map(item => {
                return(
                    <div>
                        <div className="list" key={item.noteid} >
                            <p style={{
                                textDecoration: item.done === 1  ? 'line-through' : ""
                            }}  key={item.noteid} onClick={()=> this.props.toggleComplete(item) } > {item.todo} {item.noteid} </p>
                            <div className="icons">

                                <Button onClick ={this.handleModal}>Edit</Button>
                                <Modal show={this.state.show} onHide={this.handleModal} >
                                    <Modal.Header closeButton>Edit Todo</Modal.Header>
                                    <Modal.Body>
                                        <Row>
                                            <Col sm={6}>
                                                <Form onSubmit={this.handleSubmit}>
                                                <Form.Group controlId="TodoName" >
                                                    <Form.Label>Re-enter Todo</Form.Label>
                                                    <Form.Control type="text" name="TodoName" required placeholder="TodoName" />
                                                </Form.Group>
                                                <Form.Group>
                                                    <Button variant="primary" type="submit">Rename</Button>
                                                </Form.Group>
                                                </Form>
                                                <p  id="currentNoteId">{this.state.currentNoteID}</p>
                                            </Col>
                                        </Row>
                                    </Modal.Body>
                                    <Modal.Footer>
                                        <Button onClick ={this.handleModal}>Close</Button>
                                    </Modal.Footer>
                                </Modal>


                                <img onClick={()=> this.props.deleteItem(item.noteid)} src="https://img.icons8.com/wired/64/000000/empty-trash.png" height="20" />
                            </div>
                        </div>
                    </div>

                )
            })}
        </div>

    }
}


