import React from 'react';
import ListItem from './ListItem';
import './Note.css'
class Note extends React.Component {
    state={
        currentItem:{
            text:'',
            key:''
        }
    }

    handleInput =(e) => {
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        })
    }

    addItem = (e) => {
        e.preventDefault()
        const newItem = this.state.currentItem
        if(newItem.text !== ""){
            const newItems = [...this.state.items, newItems]
            this.setState({
                items:newItems,
                currentItem:{
                    text:"",
                    key: ""
                }
            })
        }
    }

    render(){
        return(
            <div className="form">
                <header>
                    <form id="to-do-form">
                        <input 
                            onSubmit={()=> this.props.addItem(this.state.currentItem)}
                            type="text" 
                            placeholder="Enter Text" 
                            value={this.state.currentItem.text}
                            onChange={this.handleInput}
                        />
                        <button type="submit">Add</button>
                    </form>
                </header>
                <ListItem />
            </div>

        )
    }
}

export default Note
