import React from 'react'
import Particles from 'react-particles-js'
import './ListItem.css'
import Note from './Note'

class ListItem extends React.Component{

    state = {
        editing:false
    }

    handleClick = (e) =>{
        console.log(e)
        this.setState({editing:true},console.log(this.state.editing))
    }

    render(){

        const viewStyle= {}
        const editStyle={}

        if (this.state.editing){
            viewStyle.display = 'none'
        } else {
            editStyle.display = 'none'
        }

        return <div>
            {this.props.items.map(item => {
                return(
                    <div>
                        <div className="list" style={viewStyle} key={item.noteid}>
                            <p key={item.noteid}> {item.todo}</p>
                            <div className="icons">
                                <img onClick={this.handleClick} className="pencil" src="https://img.icons8.com/android/24/000000/pencil.png" height="20"/>
                                <img onClick={()=> this.props.deleteItem(item.noteid)} src="https://img.icons8.com/wired/64/000000/empty-trash.png" height="20" />
                            </div>
                        </div>

                        <div className="list editStyle" styles={editStyle} key={item.noteid}>
                            <input type='text' value={item.todo} />
                        </div>
                    </div>

                )
            })}
        </div>

    }
}


